import shutilimport oscount = 1file_path = str(count) + '.jpg'src = 'D:/waste_data/roi/hoil/hoil/'dir = 'D:/waste_data/roi/hoil/hoil_roi/'# for dirl in os.listdir(src):#     file_list = os.listdir(src+dirl)a = os.listdir(src)a.sort()for file in a:    if file.endswith('.jpg'):        if count < 10:            file_path = '000'+ str(count) + '.jpg'        elif count < 100:            file_path = '00'+ str(count) + '.jpg'        elif count < 1000:            file_path = '0' + str(count) + '.jpg'        else:            file_path = str(count) + '.jpg'        shutil.move(src+file, dir+file_path)        count += 1